RAISERROR('		...insert default data', 10, 1) WITH NOWAIT
GO
SET NOCOUNT ON
GO
INSERT	INTO [dbo].[catalogReportHTMLGraphics]([name], [reference_url], [tooltip], [image_data_base64])
				  SELECT 'Logo' AS [name]	, NULL AS [reference_url], NULL AS [tooltip], N'<img style="border-style: none" alt="Logo" width="188" height="60" src="data:image/png;base64,"/>' AS [image_data_base64]
		UNION ALL SELECT 'check-checked'	, NULL AS [reference_url], NULL AS [tooltip], N'<img style="border-style: none" alt="" src="data:image/png;base64," />' AS [image_data_base64]
		UNION ALL SELECT 'check-unchecked'	, NULL AS [reference_url], NULL AS [tooltip], N'<img style="border-style: none" alt="" src="data:image/png;base64," />' AS [image_data_base64]
		UNION ALL SELECT 'radio-checked'	, NULL AS [reference_url], NULL AS [tooltip], N'<img style="border-style: none" alt="" src="data:image/png;base64," />' AS [image_data_base64]
		UNION ALL SELECT 'radio-unchecked'	, NULL AS [reference_url], NULL AS [tooltip], N'<img style="border-style: none" alt="" src="data:image/png;base64," />' AS [image_data_base64]
GO
